import"./main-C1MXJev5.js";const a=document.querySelectorAll("#ej-5 button"),r=document.getElementById("resultado"),c=document.getElementById("numero1"),i=document.getElementById("numero2");a.forEach(o=>{o.addEventListener("click",()=>{const t=parseFloat(c.value),e=parseFloat(i.value),s=o.dataset.operacion;if(isNaN(t)||isNaN(e)){r.textContent="Introduce números válidos";return}let n=0;switch(s){case"sumar":n=t+e;break;case"restar":n=t-e;break;case"multiplicar":n=t*e;break;case"dividir":n=e===0?"Dividir por cero es infinito":t/e;break}r.textContent=n})});
